  color: white;
`

const Container = styled.div`
  display: flex;
  width: 100%;
`
const Left_box = styled.div`
`
const Right_box = styled.div`
  display: flex;
`

class Movie extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
    };
  }
  render() {
    return (
      <li>
        <div>
          <div>
            {this.props.name}
          </div>
          <div>
            <div>
              {this.props.money}
            </div>
          </div>
        </div>
      </li>
    );
  }
}


class MyComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      movies: []
    };
  }

  componentDidMount() {
    fetch("https://tkak5.github.io/movie/data/2019.json"
    ).then((response) => {if(response.ok) {
        response.json().then((json) =>
          this.setState({
            isLoaded: true,
            movies: json[2019]
          })
        )
      } else {
        this.setState({
          isLoaded: true,
          error: response
        });
      }
    });
  }

  componentDidUpdate() {
    var movie_list = this.state.movies;
    for (var i; i < movie_list.length; i++) {
      var movie_color = document.getElementById(movie_list[i]['title']);
      movie_color.style.color = 'hotpink';
    }
  }

  render() {
    const { error, isLoaded, movies } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <ul>
          {movies.map(movie => (
            <Movie
              name={movie.title}
              money={movie.worldwide}
            />
            ))
          }
        </ul>
      );
    }
  }
}
// ========================================

ReactDOM.render(
  <MyComponent />,
  document.getElementById('root')
);
