import React from 'react';
import ReactDOM from 'react-dom';
//import styled from 'styled-components';
import './index.css';

const styleGenerator = (size) => ({
  width: size
});

class Movie extends React.Component {
  render() {
    if (this.props.error) {
      return <div>Error: {this.props.error.message}</div>;
    } else if (!this.props.isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        /*
        <ul>
          {this.props.titles["movies"].map(movie => (
            <li>
              <div className="wrapper">
                <div className="left_box">
                  {movie.title}
                </div>
                <div className="center_box">
                  $ {movie.worldwide.toLocaleString()}
                </div>
                <div className="right_box">
                  <div className="bar" style={styleGenerator(movie.worldwide/this.props.titles["max"] * 500)}>
                  </div>
                </div>
              </div>
            </li>
          ))
          }
        </ul>
        */
        <div>
          {this.props.titles}
        </div>
        );
    }
  }
}



class Ajax extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      year: "2019",
      error: null,
      isLoaded: false,
      movies: [],
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({year: event.target.value});
  }

  componentDidMount() {
    var movie_list = {}
    for(var i = 0; i < 43; i++) {
      const thisyear = String(2019-i);
      fetch("https://tkak5.github.io/movie/data/" + thisyear + ".json"
      ).then((response) => {if(response.ok) {
          response.json().then((json) => {
            movie_list[thisyear] = json
            });
          } else {
            this.setState({
              isLoaded: true,
              error: response
            });
          }
      });
    }
    this.setState({
      isLoaded: true,
      movies: movie_list
    });
  }

  renderMovie() {
    return (
      <Movie
        error={this.state.error}
        isLoaded={this.state.isLoaded}
        titles={this.state.movies[this.state.year]}
      />
    );
  }

  render() {
    return(
      <div>
        <form>
          <select value={this.state.data} onChange={this.handleChange}>
            <option value="2019">2019</option>
            <option value="2018">2018</option>
          </select>
        </form>
        <div>
          {this.renderMovie()}
        </div>
      </div>
    );
  }
}


// ========================================

ReactDOM.render(
  <Ajax />,
  document.getElementById('root')
);
