import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class MyComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      movies: []
    };
  }

  componentDidMount() {
    fetch("https://api.example.com/items")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            movies: result.items
          });
        },
        // 補足：コンポーネント内のバグによる例外を隠蔽しないためにも
        // catch()ブロックの代わりにここでエラーハンドリングすることが重要です
      )
  }

  render() {
      return (
        <ul>
          {movies.map(movie => (
            <li key={movie.name}>
              {movie.name} {movie.price}
            </li>
          ))}
        </ul>
      );
    }
  }
// ========================================

ReactDOM.render(
  <MyComponent />,
  document.getElementById('root')
);
