import React from 'react';
import ReactDOM from 'react-dom';
import styled from 'styled-components';


class Movie extends React.Component {
  render() {
    return (
      <li id={this.props.name}>{this.props.name} {this.props.money}</li>
      );
  }
}

class MyComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      movies: []
    };
  }

  componentDidMount() {
    fetch("https://tkak5.github.io/movie/data/2019.json"
    ).then((response) => {if(response.ok) {
        response.json().then((json) =>
          this.setState({
            isLoaded: true,
            movies: json[2019]
          })
        )
      } else {
        this.setState({
          isLoaded: true,
          error: response
        });
      }
    });
  }

  componentDidUpdate() {
    var movie_list = this.state.movies;
    for (var i; i < movie_list.length; i++) {
      var movie_color = document.getElementById(movie_list[i]['title']);
      movie_color.style.color = 'hotpink';
    }
  }

  render() {
    const { error, isLoaded, movies } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <ul>
          {movies.map(movie => (
            <Movie
              name={movie.title}
              money={movie.worldwide}
            />
            ))
          }
        </ul>
      );
    }
  }
}
// ========================================

ReactDOM.render(
  <MyComponent />,
  document.getElementById('root')
);
