class MyComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      movies: []
    };
  }

  componentDidMount() {
    fetch("https://tkak5.github.io/movie/data/2019.json"
    ).then((response) => {if(response.ok) {
        response.json().then((json) =>
          this.setState({
            isLoaded: true,
            movies: json["movies"],
            max: json["max"]
          })
        )
      } else {
        this.setState({
          isLoaded: true,
          error: response
        });
      }
    });
  }

  componentDidUpdate() {
    var movie_list = this.state.movies;
    for (var i; i < movie_list.length; i++) {
      var movie_color = document.getElementById(movie_list[i]['title']);
      movie_color.style.color = 'hotpink';
    }
  }

  render() {
    const { error, isLoaded, movies, max } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <ul>
          {movies.map(movie => (
            <li>
              <div className="container">
                <div className="left_box">
                  {movie.title}
                </div>
                <div className="right_box">
                  <div className="bar" style={{
                    width: {max}}}>
                    {movie.worldwide}
                  </div>
                </div>
              </div>
            </li>
              //name={movie.title}
              //money={movie.worldwide}
              //ratio={Number(movie.worldwide / max)}
            ))
          }
        </ul>
      );
    }
  }
}
// ========================================

ReactDOM.render(
  <MyComponent />,
  document.getElementById('root')
);
