import React from 'react';
import ReactDOM from 'react-dom';
import styled from 'styled-components';
import './index.css';
/*
const styleGenerator = (size) => ({
  width: size
});
*/

class Movie extends React.Component {


  render() {
    const ratio = this.props.money / this.props.max;
    const Bar = styled.div.attrs(props => ({
      width: props.size
    }))
    return (
      <li>
        <div className="wrapper">
          <div className="left_box">
            {this.props.name}
          </div>
          <div className="center_box">
            {this.props.money}
          </div>
          <div className="right_box">
            <Bar className="bar" size={ratio}>
            </Bar>
          </div>
        </div>
      </li>
    );
  }
}


class MyComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      movies: []
    };
  }

  componentDidMount() {
    fetch("https://tkak5.github.io/movie/data/2019.json"
    ).then((response) => {if(response.ok) {
        response.json().then((json) =>
          this.setState({
            isLoaded: true,
            movies: json["movies"],
            max: json["max"]
          })
        )
      } else {
        this.setState({
          isLoaded: true,
          error: response
        });
      }
    });
  }

  componentDidUpdate() {
    var movie_list = this.state.movies;
    for (var i; i < movie_list.length; i++) {
      var movie_color = document.getElementById(movie_list[i]['title']);
      movie_color.style.color = 'hotpink';
    }
  }

  render() {
    const { error, isLoaded, movies, max} = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <ul>
          {movies.map(movie => (
            <Movie
              name={movie.title}
              money={movie.worldwide}
              max={max}
            />
            ))
          }
        </ul>
      );
    }
  }
}
// ========================================

ReactDOM.render(
  <MyComponent />,
  document.getElementById('root')
);
