import React from 'react';
import ReactDOM from 'react-dom';
//import styled from 'styled-components';
import './index.css';
import Sort from './sort';

const styleGenerator = (size) => ({
  width: size
});

export default class Movie extends React.Component {
  render() {
    if (this.props.error) {
      return <div>Error: {this.props.error.message}</div>;
    } else if (!this.props.isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
      /*
        <ul>
          {this.props.movies.movies.map(movie => (
            <li key={movie.income.toString()}>
              <div className="wrapper">
                <div className="left_box">
                  <div className="rank">
                    {movie.rank}
                  </div>
                  <div className="title">
                    {movie.title}
                  </div>
                </div>
                <div className="center_box">
                  $ {movie.income.toLocaleString()}
                </div>
                <div className="right_box">
                  <div className="bar" style={styleGenerator(movie.income/this.props.movies.max * 500)}>
                  </div>
                </div>
              </div>
            </li>
          ))
          }
        </ul>
        */
        <div>
          {this.props.movies.movies}
        </div>
        );
    }
  }
}



class Ajax extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      year: "2019",
      error: null,
      isLoaded: false,
      movies: [],
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({year: event.target.value});
  }

  componentDidMount() {
    fetch("https://tkak5.github.io/movie/year_list.json"
    ).then((response) => {if(response.ok) {
        response.json().then((json) =>
          this.setState({
            isLoaded: true,
            movies: json,
          })
        )
      } else {
        this.setState({
          isLoaded: true,
          error: response
        });
      }
    });
  }

  renderSort() {
    return (
      <Sort
        error={this.state.error}
        isLoaded={this.state.isLoaded}
        movies={this.state.movies[this.state.year]}
      />
    );
  }

  render() {
    var list = [];

    for (var i=0; i<43; i++) {
      list.push(<option key={i.toString()} value={2019-i}>{2019-i}</option>);
    }
    return(
      <div>
        <form>
          <select value={this.state.year} onChange={this.handleChange}>
            {list}
          </select>
        </form>
        <div>
          {this.renderSort()}
        </div>
      </div>
    );
  }
}


// ========================================

ReactDOM.render(
  <Ajax />,
  document.getElementById('root')
);
