import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import Sort from './sort';

const sidesize = 500;
const styleGenerator = (size) => ({
  width: size
});

export default class Movie extends React.Component {
  render() {
    return (
      <ul>
        {this.props.movies.map(movie => (
          <li key={movie.income.toString() + movie.title.toString()}>
            <div className="wrapper">
              <div className="left_box">
                <div className="rank">
                  {movie.rank}
                </div>
                <div className="title">
                  {movie.title}
                </div>
              </div>
              <div className="center_box">
                $ {movie.income.toLocaleString()}
              </div>
              <div className="right_box">
                <div className="bar" style={styleGenerator(movie.income/this.props.max * sidesize)}>
                </div>
              </div>
            </div>
          </li>
        ))
        }
      </ul>
    );
  }
}




class Ajax extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      year: "2019",
      error: null,
      isLoaded: false,
      movies: [],
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({year: event.target.value});
  }

  componentDidMount() {
    fetch("https://tkak5.github.io/movie/year_list.json"
    ).then((response) => {if(response.ok) {
        response.json().then((json) =>
          this.setState({
            isLoaded: true,
            movies: json,
          })
        )
      } else {
        this.setState({
          isLoaded: true,
          error: response
        });
      }
    });
  }

  renderSort() {
    if (this.state.error) {
      return <div>Error: {this.state.error.message}</div>
    } else if (!this.state.isLoaded) {
      return <div>Loading...</div>
    } else {
      return (
        <Sort
          movies={this.state.movies}
        />
      );
    }
  }

  render() {
    return(
      <div>
        <div>
          {this.renderSort()}
        </div>
      </div>
    );
  }
}


// ========================================

ReactDOM.render(
  <Ajax />,
  document.getElementById('root')
);
