import React, { useState, useMemo } from 'react';
import Movie from './index';
import Button from './button';


const Sort = (props) => {
  const [sort, setSort] = useState([])

  const KEYS = ["rank", "title", "income"];

  const sortedMovies = useMemo(() => {
    let _sortedMovies = props.movies.movies;
    if (sort.key) {
      _sortedMovies = _sortedMovies.sort((a, b) => {
        a = a[sort.key];
        b = b[sort.key];

        if(a === b) {
          return 0;
        }
        if(a > b) {
          return 1 * sort.order;
        }
        if(a < b) {
          return -1 * sort.order;
        }
      });
    }
    return _sortedMovies;
  }, [sort]);

  const handleSort = (key) => {
    if (sort.key === key) {
      setSort({...sort, order: -sort.order });
    } else {
      setSort({
        key: key,
        order: 1
      })
    }
  };

   return(
    <div>
      <div>
        {KEYS.map((key, index) => (
          <Button
            key={index}
            button={key}
            sort={sort}
            handleSort={handleSort}
          />
        ))
        }
      </div>
      <div>
        <Movie
          movies={props.movies}
          max={props.max}
        />
      </div>
    </div>

   );
}

export default Sort;
