import React from 'react';
import ReactDOM from 'react-dom';
import styled from 'styled-components/native';

const li = styled.li`
  color : hotpink;
`


class MyComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      movies: []
    };
  }

  componentDidMount() {
    fetch("https://tkak5.github.io/movie/data/2019.json"
    ).then((response) => {if(response.ok) {
        response.json().then((json) =>
          this.setState({
            isLoaded: true,
            movies: json[2019]
          })
        )
      } else {
        this.setState({
          isLoaded: true,
          error: response
        });
      }
    });
  }

  render() {
    const { error, isLoaded, movies } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <ul>
          {movies.map(movie => (
            <li className="sample" key={movie.title}>
              {movie.title} {movie.worldwide}
            </li>
          ))}
        </ul>
      );
    }
  }
}
// ========================================

ReactDOM.render(
  <MyComponent />,
  document.getElementById('root')
);
